import React from 'react';
import './CarDetails.css'

const CarDetails = ()=> {
    const colorOption=["white","black","red","yellow","green","blue"];
    const Style =(color)=>
    {
        let font= "black";
        if(color==="black")
        font="white"
        
      return{
        borderRadius: 12,
        width: 112,
        height: 44,  
        backgroundColor : color,
        color: font,
     
        marginTop: 10
        
    }
}
const Clicked = (e)=> {
     e.preventDefault();
     let i= e.target.value
    
     const buttonList= document.querySelectorAll('.car-details-color-box button')
    console.log(buttonList[i])
    buttonList[i].style.border="3px solid black"
    
    
}
String.prototype.capitalize = function() {
    return this.charAt(0).toUpperCase() + this.slice(1);
}
 
let colorID= -1;
return(

<>
<div className="body"> 

   <div className="car-details-title">
       Car Registration
   </div>
   <form className="car-details-form">
        Company <span style={{color: "red"}}>*</span><br/>
        <input type="text" className="car-details-input" placeholder="Type your Company name"/> <br/>
        Model Name <span style={{color: "red"}}>*</span><br/>
        <input type="text" className="car-details-input" placeholder="Type your Model name"/><br/>  
        Car Plate Number <span style={{color: "red"}}>*</span><br/>
        <input type="text" className="car-details-input" placeholder="Type your Car Plate Number"/>  <br/> <br/>
  
  
        Select your option <span style={{color: "red"}}>*</span><br/> 
        <div className="car-details-color-box">
            {colorOption.map((color, i) => {     
                            
            return (<button type="none" key={i} value={i} style={Style(color)} onClick={Clicked} >{color.capitalize()}</button>) 
            })}

        </div>
   <input type="hidden" value={colorID}/>
   <button type="submit"> Register Now </button>
   
   </form>

</div>
</>
)
}
export default  CarDetails
