const ColorBox = (props)=> {
const Style =()=>
{
    let font= "black";
    if(props.color=="black")
    font="white"
    
  return{
    borderRadius: 12,
    width: 112,
    height: 44,  
    backgroundColor : props.color,
    color: font,
    display: 'flex',
    justifyContent: "center",
    alignItems: "center",
    marginTop: 10
    
}
}
return(
